  //   return () => clearTimeout(timer);
  // });
  const handleClick=(e)=>{
    console.log("button is cliked");
    setVal(val+1)
  }
  useEffect(()=>{
    console.log("called");
    setVal(val+1)
  },[]);
  return (
    <div>
      <h1>useEffect Example: {time.toLocaleTimeString()}</h1>
      <button onClick={(e)=> {handleClick(e);} }>{val}</button>
    </div>
  );
};

