import { useReducer, useState } from "react"

const initialState={
    counter:0
}
const reducer = (state,action) =>{
    switch(action.action){
        case "INCREASE":
            console.log("called increament");
            return {
                ...state,
                counter:action.counter+1,
            }
        case "DECREASE":
            return {
                ...state,
                counter:action.counter-1,
            }
        default :
            return state;
    }
}


const Counter = () =>{
    const [counter,dispatch] = useReducer(reducer,0);
    const handleClick = () =>{
        console.log("called the click");
    }
    return (
        <div>
            Hi
            <h1>This is the counter : {counter}</h1>
            <button onClick={()=>dispatch({type:"INCREASE",counter:counter})}><strong>+</strong></button>
            <button onClick={()=>handleClick()}><strong>-</strong></button>
        </div>
    )
}

export default Counter;
