import { useReducer, useState } from "react"

const initialState={
    counter:0
}
const reducer = (state = initialState,action) =>{
    switch(action.action){
        case "INCREASE":
            return {
                ...state,
                counter:action.counter+1,
            }
        case "DECREASE":
            return {
                ...state,
                counter:action.counter-1,
            }
        default :
            return state;
    }
}
const Counter = () =>{
    const [counter,counterDispatcher] = useReducer(reducer,0);
    return (
        <div>
            Hi
            <h1>This is the counter : {counter}</h1>
            <button onClick={()=>{counterDispatcher({type:"INCREASE",counter:counter})}}><strong>+</strong></button>
            <button onClick={()=>{}}><strong>-</strong></button>
        </div>
    )
}

export default Counter;
